definition:
{"node": {
    "notes": "GPS augmented by a network of fixed ground stations that continuously broadcast the difference between their true locations and the location determined from the satellites.",
    "accuracy": "better than 100m to better than 10m"
}}

{"node": {
    "notes": "Global navigation based on a network of satellites deployed and maintained by the USA.",
    "accuracy": "better than 100m"
}}

{"node": {
    "mode": "Doppler-derived Argos localisation",
    "accuracy": "better than 250m (class 3) to worse than 1500m (class 0)"
}}

{"node": {
    "mode": "GPS localisation",
    "accuracy": "better than 100m"
}}

{"node": {
    "mode": "Doppler-derived localisation",
    "accuracy": "better than 10km or 2km if combined with relative water velocity em-log"
}}

{"node": {
    "notes": "Family of microwave short-range positioning systems with vessel equipment and three shore transponders (master and two slaves) set up specifically for surveys.",
    "range": "80km",
    "accuracy": "1m (resolution 0.1m)"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Global navigation based on transmitters in several European countries, Japan, Canada (until 2002-10) and USA (until 2010-02-08).",
    "range": "950kmto 1700km (day): 650km to 1250km (day)",
    "operational": "1947 to 2010. Maintained as GPS backup in the EU.",
    "frequency": "70-130kHz"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Mostly used for aircraft.",
    "range": "150-200km",
    "accuracy": "better than 20m",
    "frequency": "around 2MHz"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Survey positioning system.",
    "range": "450km",
    "accuracy": "4-7m down to 20m at maximum range",
    "operational": "from 1977 (mid-80s in Europe)",
    "frequency": "1.6-2MHz"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Global long-range but low accuracy navigation system",
    "range": "up to 15000km",
    "accuracy": "better than 7.4km",
    "operational": "1968-1997",
    "frequency": "10-14kHz"
}}

{"node": {
    "mode": "hyperbolic (usual), circular or combined",
    "notes": "Racal positioning system similar to Decca HiFix based on temporary or permanent transmitter chains comprising a master plus at least two slaves.",
    "range": "250km (night) to 700km (day)",
    "accuracy": "up to 0.5-1m",
    "operating": "until 2000",
    "frequency": "1.6-3.4MHz"
}}

{"node": {
    "mode": "hyperbolic",
    "frequency": "around 300-400kHz"
}}

{"node": {
    "mode": "hyperbolic or circular",
    "notes": "Navigation and positioning system for the North Sea petroleum industry.",
    "range": "400km hyperbolic; 75-100km circular",
    "accuracy": "better than 2m below horizon (10m above horizon)",
    "operational": "1980s to 1995",
    "frequency": "420-450MHz"
}}

{"node": {
    "mode": "circular",
    "notes": "Survey positioning system.",
    "range": "150km",
    "accuracy": "better than 3m",
    "frequency": "420-450MHz"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Not for general navigation.  Chains usually set up for specific surveys.",
    "range": "under 100km",
    "accuracy": "better than 10m: maximum accuracy at 50km range is 1.5m",
    "operating": "1962 to 2000",
    "frequency": "1.6MHz"
}}

{"node": {
    "mode": "hyperbolic",
    "range": "250km to 750km",
    "accuracy": "30m to 500m",
    "operational": "1970s to late 1990s",
    "frequency": "100kHz"
}}

{"node": {
    "mode": "hyperbolic",
    "notes": "Transmitter groups, each with a master and three slaves",
    "range": "450km (day) to 750km (night)",
    "accuracy": "50m(day) to 200m (night) in good conditions down to 2km near range limit",
    "operating": "WWII to 2001-03 (2000-03-31 in UK)",
    "frequency": "70 and 130kHz"
}}

{"node": {
    "notes": "A family of short-wave receivers that determine position by determining the line-of-sight distance from 3 or more transmitters set up for the survey.",
    "range": "up to 75km",
    "accuracy": "up to 2m",
    "frequency": "5410-5890MHz"
}}

{"node": {
    "notes": "Real Time Kinematic (RTK) satellite navigation technique based on the use of carrier phase measurements of the GPS signals where a single reference station or a network of reference stations provides the real-time corrections.",
    "accuracy": "Horizontal Accuracy: Fixed RTK mode: 1-5cm, Float RTK mode: 20cm-1m. Vertical accuracy : twice the horizontal accuracy",
    "operating": "Since the mid-1990s"
}}
